C = intersectionpoint(arc(A,22,0,180),arc(B,27,0,180));
P = intersectionpoint(arc(A,10 + r,0,180),arc(B,15 + r,0,180));

draw(Circle(A,10));
draw(Circle(B,15));
draw(Circle(C,12));
draw(Circle(P,r));
draw(A--(A + (0,-10)));
draw(B--(B + (15,0)));
draw(C--(C + (0,12)));
draw(P--(P + (r,0)));

label("$a$", A + (0,-10)/2, W);
label("$b$", B + (15,0)/2, S);
label("$c$", C + (0,12)/2, W);
label("$r$", P + (r,0), E);
[/asy]

